class AST_Num (AST):
	op, is_num = '#', True

	_rec_int          = re.compile (r'^-?\d+$')
	_rec_pos_int      = re.compile (r'^\d+$')
	_rec_mant_and_exp = re.compile (r'^(-?\d*\.?\d*)(?:[eE]([+-]?\d+))?$')

	def _init (self, num):
		self.num = num

	_is_pos_num   = lambda self: self.num [0] != '-'
	_is_neg_num   = lambda self: self.num [0] == '-'
